/*

To filter process.argv based on commands array in commands.js

*/

'use strict';

var cliFilter = function (argv, config, commands) {
  for (var c = 0; c < commands.length; c++) {
    for (var a = 0; a < argv.length; a++) {
      if (argv[a].slice(0, commands[c].command[0].length) === commands[c].command[0] ) {
        commands[c].exec(config, argv[a].slice(commands[c].command[0].length));
        break;
      } else if (argv[a].slice(0, commands[c].command[1].length) === commands[c].command[1] ) {
        commands[c].exec(config, argv[a].slice(commands[c].command[1].length));
        break;
      }
    }
  }
  return config;
}

module.exports = cliFilter;
